<?php
add_action('after_setup_theme', 'theme_setup'); function theme_setup() { date_default_timezone_set('Asia/Tehran'); add_theme_support('title-tag'); add_theme_support('post-thumbnails'); add_image_size('sy-size',200,200,['center','center']); add_filter('show_admin_bar', '__return_false'); register_nav_menu('top nav','منو اصلی بالای سایت'); require_once get_template_directory() . '/class/nav-walker/WP_Bootstrap_Navwalker.php'; } add_filter('use_block_editor_for_post', '__return_false'); add_filter('use_widgets_block_editor', '__return_false'); add_filter( 'upload_mimes', 'filter_allowed_mimes_for_avif'); function filter_allowed_mimes_for_avif( $mime_types ) { $mime_types['avif'] = 'image/avif'; return $mime_types; } 