<?php
 add_filter('mce_external_plugins', 'add_tinymce_plugin_js'); add_filter('mce_buttons', 'register_custom_button_for_tinymce'); add_filter('tiny_mce_before_init','format_tinymce'); function add_tinymce_plugin_js($array) : array { $keys = ['video', 'quote','shortcodes']; return array_fill_keys($keys, get_template_directory_uri() . '/assets/js/tinymce-button.js'); } function register_custom_button_for_tinymce($buttons) { array_push($buttons, 'video', 'quote','shortcodes'); return $buttons; } function format_tinymce($in) { $in['toolbar1'] = 'formatselect,bold,italic,bullist,numlist,blockquote,alignleft,aligncenter,alignright,link,wp_more,spellchecker,wp_adv,dfw,ltr,hr,redo,undo,video,quote,shortcodes'; return $in; } 