<?php
 add_action('wp_ajax_sy_more_content', 'sy_more_content'); add_action('wp_ajax_nopriv_sy_more_content', 'sy_more_content'); function sy_more_content() { if (!isset($_POST['_nonce']) && !wp_verify_nonce($_POST['_nonce'])) { wp_send_json([ 'error' => true, 'message' => 'دسترسی غیر مجاز است', ], 403); } $args = [ 'post_type' => $_POST['pageSlug'], 'posts_per_page' => 3, 'paged' => $_POST['paged'], 'status' => 'publish' ]; $the_query = new WP_Query($args); if ($the_query->have_posts()) : $html_output = ''; while ($the_query->have_posts()): $the_query->the_post(); $html_output .= sy_archive_filter_html_output(); endwhile; wp_send_json([ 'success' => true, 'message' => 'مطالب با موفقیت ارسال شد', 'content' => $html_output, 'max_page' => $the_query->max_num_pages, ], 200); else: wp_send_json([ 'error' => true, 'message' => 'پستی یافت نشد', ], 404); endif; wp_reset_postdata(); }