<?php
 add_action('wp_ajax_video', 'video'); add_action('wp_ajax_nopriv_video', 'video'); function video() { if (!wp_verify_nonce($_POST['_nonce'])) { wp_send_json([ 'error' => true, 'message' => 'access denied' ], 403); } $args = [ 'post_type' => ['tech'], 'showposts' => 3, 'meta_key' => '_sy_post_types', 'meta_value' => '1' ]; $the_query = new WP_Query($args); if ($the_query->have_posts()): $html_output = ''; while ($the_query->have_posts()):$the_query->the_post(); $html_output .= sy_tech_html_output(); endwhile; wp_send_json([ 'success' => true, 'message' => 'فیلتر محتوا بر اساس پست ویدیوئی با موفقیت اعمال گردید', 'content' => $html_output ], 200); else: wp_send_json([ 'error' => true, 'message' => 'پست ویدیوئی یافت نشد', ], 403); endif; wp_reset_postdata(); }